import { CheckCard } from '@ant-design/pro-card';
import ProForm, { ProFormInstance, ProFormItem, ProFormText } from '@ant-design/pro-form';
import { PageContainer } from '@ant-design/pro-layout';
import { Block } from '@uiw/react-color';
import { Avatar, Button, Card, Col, Image, Row, Space, Switch, message } from 'antd';
import React, { useRef, useState } from 'react';
import { fakeSubmitForm, queryFakeList } from './service';
import styles from './style.less';

const CardList = () => {
  const [messageApi, contextHolder] = message.useMessage();
  const [logoPreview, setLogoPreview] = useState();
  const [faviconPreview, setFaviconPreview] = useState();
  const [bannerPreview, setBannerPreview] = useState();
  const [lang, setLang] = useState<boolean>();
  const [langLoading, setLangLoading] = useState(false);
  const [hex, setHex] = useState('#fff');
  const formI18nRef = useRef<ProFormInstance>();
  const formThemeRef = useRef<ProFormInstance>();
  const formLoginRef = useRef<ProFormInstance>();

  React.useEffect(() => {
    queryFakeList({ keys: ['authsaur.theme', 'authsaur.i18n', 'authsaur.login', 'authsaur.lang'] }).then(
      function (resp) {
        console.log(resp);
        const response = resp?.data || {};
        console.log(response);
        formI18nRef?.current?.setFieldsValue({
          ...response['authsaur.i18n'],
        });
        formThemeRef?.current?.setFieldsValue({
          ...response['authsaur.theme'],
        });
        formLoginRef?.current?.setFieldsValue({
          ...response['authsaur.login'],
        });
        setLogoPreview(response['authsaur.theme']?.logo);
        setFaviconPreview(response['authsaur.theme']?.favicon);
        setHex(response['authsaur.theme']?.color);
        setBannerPreview(response['authsaur.login']?.banner);
        setLang(response['authsaur.lang']?.enabled == 'true');
      },
    );
  }, []);

  return (
    <PageContainer>
      {/* <Card bordered={false}> */}
      <Row gutter={16} >
        <Col xs={24} lg={12}>
          <div style={{ display: 'flex', flex: 1, flexDirection: 'column', height: '100%' }}>
            <Card title="语言">
              <Space>
                中英文语言切换
                <Switch
                  checked={lang}
                  loading={langLoading}
                  onChange={(checked, e) => {
                    setLangLoading(true);
                    // setLang(!checked);
                    console.log(checked, e);
                    fakeSubmitForm({ key: 'authsaur.lang', value: { enabled: checked } }).then(
                      (response) => {
                        setLangLoading(false);
                        if (response.success == true) {
                          message.success({ content: '更新成功', className: 'box_success' });
                          setLang(checked);
                          return true;
                        } else {
                          setLang(!checked);
                          message.error({
                            content: '更新失败:' + response?.msg,
                            className: 'box_error',
                          });
                          return false;
                        }
                      },
                    );
                  }}
                />
              </Space>
            </Card>
            <Card
              title="文案"
              style={{
                marginTop: '24px',
                display: 'flex',
                flex: 1,
                flexDirection: 'column',
                justifyContent: 'start',
              }}
            >
              <ProForm<{
                name: string;
                company?: string;
                useMode?: string;
              }>
                submitter={{
                  render: (props, doms) => {
                    return [
                      <Button type="primary" key="submit" onClick={() => props.form?.submit?.()}>
                        保存设置
                      </Button>,
                    ];
                  },
                }}
                onFinish={async (values) => {
                  const response = await fakeSubmitForm({ key: 'authsaur.i18n', value: values });
                  if (response.success == true) {
                    message.success({ content: '更新成功', className: 'box_success' });
                    return true;
                  } else {
                    message.error({ content: '更新失败:' + response?.msg, className: 'box_error' });
                    return false;
                  }
                }}
                formRef={formI18nRef}
                autoFocusFirstInput
              >
                <ProFormText
                  label="系统名称"
                  // width="xl"
                  name="zh_CNsystemName"
                />

                <ProFormText
                  label="公司名称"
                  // width="xl"
                  name="zh_CNcompanyName"
                />
                <ProFormText
                  label="版权"
                  // width="xl"
                  name="zh_CNcopyright"
                />
                {/* <ProFormTextArea
                  label="公告"
                  // width="xl"
                  name="zh_CNnotice"
                /> */}
                <ProFormText
                  label="系统英文名称"
                  // width="xl"
                  name="ensystemName"
                />
                <ProFormText
                  label="公司英文名称"
                  // width="xl"
                  name="encompanyName"
                />
                <ProFormText
                  label="版权英文"
                  // width="xl"
                  name="encopyright"
                />
                {/* <ProFormTextArea
                  label="公告英文"
                  // width="xl"
                  name="ennotice"
                /> */}
              </ProForm>
            </Card>
          </div>
        </Col>
        <Col xs={24} lg={12}>
          <Card title="主题" style={{ height: '100%' }}>
            <ProForm<{
              name: string;
              company?: string;
              useMode?: string;
            }>
              submitter={{
                render: (props, doms) => {
                  return [
                    <Button type="primary" key="submit" onClick={() => props.form?.submit?.()}>
                      保存设置
                    </Button>,
                  ];
                },
              }}
              onFinish={async (values) => {
                console.log(values);
                const response = await fakeSubmitForm({
                  key: 'authsaur.theme',
                  value: { ...values, color: hex },
                });
                if (response.success == true) {
                  message.success({ content: '更新成功', className: 'box_success' });
                  return true;
                } else {
                  message.error({ content: '更新失败:' + response?.msg, className: 'box_error' });
                  return false;
                }
              }}
              formRef={formThemeRef}
              autoFocusFirstInput
            >
              <ProFormText
                label="系统LOGO"
                // width="xl"
                name="logo"
                fieldProps={{
                  onChange: (e) => {
                    console.log(e.target.value);
                    setLogoPreview(e.target.value);
                  },
                }}
              />
              <ProFormItem>
                <div className={styles.preview}>
                  <Image
                    width={80}
                    height={80}
                    src={logoPreview}
                    fallback="data:image/png;base64,"
                  />
                </div>
              </ProFormItem>
              <ProFormText
                label="系统Favicon"
                // width="xl"
                name="favicon"
                fieldProps={{
                  onChange: (e) => {
                    setFaviconPreview(e.target.value);
                  },
                }}
              />
              <ProFormItem>
                <div className={styles.preview}>
                  <Image
                    width={28}
                    height={28}
                    src={faviconPreview}
                    fallback="data:image/png;base64,"
                  />
                </div>
              </ProFormItem>
              {/* <ProFormText
                label="主题色"
                width="sm"
                name="color"
                hidden
              /> */}
              <ProFormItem label="主题色">
                <Block
                  color={hex}
                  colors={[
                    '#3f51b5',
                    '#2442bf',
                    '#009688',
                    '#008b02',
                    '#8bc34a',
                    '#9c27b0',
                    '#F47373',
                    '#B80000',
                    '#1890ff',
                    '#795548',
                  ]}
                  onChange={(color) => {
                    setHex(color.hex);
                  }}
                />
              </ProFormItem>
            </ProForm>
          </Card>
        </Col>
      </Row>
      <Card title="登录页" style={{ marginTop: '24px' }}>
        <ProForm<{
          name: string;
          company?: string;
          useMode?: string;
        }>
          submitter={{
            render: (props, doms) => {
              return [
                <Button type="primary" key="submit" onClick={() => props.form?.submit?.()}>
                  保存设置
                </Button>,
              ];
            },
          }}
          onFinish={async (values) => {
            console.log(values);
            const response = await fakeSubmitForm({ key: 'authsaur.login', value: values });
            if (response.success == true) {
              message.success({ content: '更新成功', className: 'box_success' });
              return true;
            } else {
              message.error({ content: '更新失败:' + response?.msg, className: 'box_error' });
              return false;
            }
          }}
          formRef={formLoginRef}
          autoFocusFirstInput
        >
          <Row gutter={16}>
            <Col xs={24} lg={12}>
              <ProFormText
                label="图片"
                // width="xl"
                name="banner"
                fieldProps={{
                  onChange: (e) => {
                    setBannerPreview(e.target.value);
                  },
                }}
              />
              <ProFormItem>
                <div className={styles.preview}>
                  <Image
                    width={300}
                    height={300}
                    src={bannerPreview}
                    fallback="data:image/png;base64,"
                  />
                </div>
              </ProFormItem>
            </Col>
            <Col xs={24} lg={12}>
              <ProFormItem label="布局" name="layout">
                <CheckCard.Group style={{ width: '100%' }}>
                  <CheckCard
                    title="经典居中布局"
                    avatar={<Avatar src="/center-bg.svg" size="large" />}
                    // description="图片和登录框居中，背景色为主题色"
                    value="2"
                  />
                  <CheckCard
                    title="经典左右布局"
                    avatar={<Avatar src="/left-right.svg" size="large" />}
                    // description="图片和登录框左右分屏"
                    value="3"
                  />
                  <CheckCard
                    title="居中布局+白色背景"
                    avatar={<Avatar src="/center.svg" size="large" />}
                    // description="图片和登录框居中"
                    value="1"
                  />
                </CheckCard.Group>
              </ProFormItem>
              <ProFormText
                label="隐私协议"
                // width="xl"
                name="protocol"
              />
              <ProFormText
                label="问题帮助"
                // width="xl"
                name="help"
              />
            </Col>
          </Row>
        </ProForm>
      </Card>
      {/* </Card> */}
    </PageContainer>
  );
};

export default CardList;
